import {SAVED_POST} from './types'

const initialState = {
    savedPosts:[]
}

const savedPosts = (state=initialState,action) => {
    switch (action.type) {
        case SAVED_POST:
            return {...state,savedPosts:state.savedPosts.concat([action.payload])}
            break;

        default:return state
    }
}

export default savedPosts;
