import {connect} from 'react-redux';
import {useState} from 'react';
import '../css/createPosts.css';


function AllPosts({posts}) {
    const [flag,setFlag] = useState(false);
     function TrueLike() {
        setFlag(!flag)
        let btn = document.querySelector('.likes_btn');
        if(flag){
            btn.classList.add('likes')
        }
        else {

        btn.classList.remove('likes')
        }
    }
    return (
        <div>
                {posts.map(post => 
                <div class="card" style={{width: "18rem"}}>
                <h3>@{post.userName}</h3>
                <h5>{post.firstname}</h5>
                <div class="card-body">
                  <p class="card-text">{post.text}</p>
                </div>
                <img  onClick={TrueLike} className='likes likes_btn' style={{width:'3rem',marginLeft:'30%'}} src="https://www.pinclipart.com/picdir/middle/212-2129874_png-file-clipart.png"/>
              </div>    
                )}

        </div>
    )
}

const mapStateToProps = state => {

    return {
        posts: state.posts.posts
    }
}

export default connect(mapStateToProps,null)(AllPosts);
