        <Router>
        <div>
            <nav>
                <Link to='/savedPosts'><span className='PostNav'>Saved Posts</span></Link>
            </nav>
                <div>           
                {posts.map(post => 
                <div class="card" style={{width: "18rem"}}>
                <h3>@{post.userName}</h3>
