                  <p class="card-text">{post.text}</p>
                </div>
                <img  onClick={() => TrueLike(post.firstname,post.text)} className='likes likes_btn' style={{width:'3rem',marginLeft:'30%'}} src="data:image/jpeg;base64,"/>
                <Switch>
                    <Route path='/savedPosts'>
                        <SavedPosts TrueLike={TrueLike} log={post.firstname} text={post.text}/>
                    </Route>
                </Switch>
              </div>    
                )}
                </div> 

        </div>
        </Router>
    )
}

const mapStateToProps = state => {

    return {
        posts: state.posts.posts
    }
}



export default connect(mapStateToProps,null)(AllPosts);
