import {SAVED_POST} from './types';

const initialState = {
    save:[],

}

const saveReducer = (state=initialState,action) => {
    switch (action.type) {
        case SAVED_POST:
            return {...state,save:state.posts.concat([action.payload])}
            break;
        default:return state
    }
}

export default saveReducer;
