import {connect} from 'react-redux';
import {useState} from 'react';


function AllPosts({posts}) {
    let likes = false;
    const TrueLike = likes => {
        likes;
        if(likes == true){
            console.log('1')
        }
        else {
            console.log(0)
        }
    }
    return (
        <div>
                {posts.map(post => 
                <div class="card" style={{width: "18rem"}}>
                <h3>@{post.userName}</h3>
                <h5>{post.firstname}</h5>
                <div class="card-body">
                  <p class="card-text">{post.text}</p>
                </div>
                <img  onClick={TrueLike} style={{width:'3rem',marginLeft:'30%'}} src="https://img.icons8.com/cotton/32/000000/facebook-like--v1.png"/>
              </div>    
                )}

        </div>
    )
}

const mapStateToProps = state => {

    return {
        posts: state.posts.posts
    }
}

export default connect(mapStateToProps,null)(AllPosts);
